mapper.select:
select

iam.item_am_score, am.ability_name, am.ability_id, am.ability_father_id from etms_item_am iam
left join etms_ability_model am on iam.am_id = am.ability_id where iam.item_id = #{subjectId}


    cam.course_am_score, am.ability_name, am.ability_id, am.ability_father_id from etms_course_am cam
    left join etms_ability_model am on cam.am_id = am.ability_id where cam.course_id = #{subjectId}


    uam.user_am_score, am.ability_name, am.ability_id, am.ability_father_id from etms_user_am uam
    left join etms_ability_model am on uam.am_id = am.ability_id where uam.user_id = #{subjectId}
